import React, { useEffect, useState } from 'react';
import './App.css';
import { Link, BrowserRouter as Router, Route } from 'react-router-dom';
import axios from 'axios';
import detectEthereumProvider from '@metamask/detect-provider';
import Web3 from 'web3';

import logo from './logo.svg';
import './App.css';
import logo1 from './vmLOGO.png';
import profile1 from'./profile.png';
import SoundWave from './soundwave1.png';
import wallet from './wallet.png';

import App2 from './App2';

const pinataApiKey = '192e364f981f4f05145c';
const pinataSecretKey = 'a87894043530d8c1dd39e3480b8962cd7f5d85070d50a8726e3c5350ac757358';

async function uploadFileToPinata(file) {
  const formData = new FormData();
  formData.append('file', file);

  try {
    const response = await axios.post(
      'https://api.pinata.cloud/pinning/pinFileToIPFS',
      formData,
      {
        headers: {
          pinata_api_key: pinataApiKey,
          pinata_secret_api_key: pinataSecretKey,
          'Content-Type': 'multipart/form-data',
        },
      }
    );

    if (response.status === 200) {
      const { IpfsHash } = response.data;
      console.log('File uploaded successfully! IPFS Hash:', IpfsHash);
      return IpfsHash;
    } else {
      console.error('Error uploading file:', response.statusText);
      throw new Error('Error uploading file');
    }
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
}

function App() {
  const [file, setFile] = useState(null);

  useEffect(() => {
    // ... existing code ...
  }, []);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    // ... existing code ...
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        <div className="App-content">
          <div className="file-upload">
            <input type="file" onChange={handleFileChange} />
            <button className="button" onClick={handleUpload}>
              Upload File
            </button>
          </div>
          <Link to="/app2">
            <button className="button">Go to App2</button>
          </Link>
        </div>
        <Route path="/app2" component={App2} />
      </div>
    </Router>
  );
}

export default App;
