import React, { useState } from 'react';
import axios from 'axios';

import logo from './logo.svg';
import './App.css';

const pinataApiKey = 'YOUR_PINATA_API_KEY';
const pinataSecretKey = 'YOUR_PINATA_SECRET_KEY';

async function uploadFileToPinata(file) {
  const formData = new FormData();
  formData.append('file', file);

  try {
    const response = await axios.post(
      'https://api.pinata.cloud/pinning/pinFileToIPFS',
      formData,
      {
        headers: {
          pinata_api_key: pinataApiKey,
          pinata_secret_api_key: pinataSecretKey,
          'Content-Type': 'multipart/form-data',
        },
      }
    );

    if (response.status === 200) {
      const { IpfsHash } = response.data;
      console.log('File uploaded successfully! IPFS Hash:', IpfsHash);
      return IpfsHash;
    } else {
      console.error('Error uploading file:', response.statusText);
      throw new Error('Error uploading file');
    }
  } catch (error) {
    console.error('Error:', error);
    throw error;
  }
}

function App() {
  const [fileSelected, setFileSelected] = useState(false);
  const [urlFile, setUrlFile] = useState('');

  const onChange = async (e) => {
    const file = e.target.files[0];

    try {
      const addedFile = await uploadFileToPinata(file);
      setUrlFile(addedFile);
      setFileSelected(true);
      console.log(`https://ipfs.io/ipfs/${addedFile}`);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <img src={logo} className="App-logo" alt="logo" />
        <p>Upload File</p>
        <input className="file-input" type="file" onChange={onChange} />
        {!fileSelected && (
          <label htmlFor="file-input" className="button">
            Select File
          </label>
        )}
        {fileSelected && <p>Selected File URL: {urlFile}</p>}
      </div>
      <div className="card">
        <img src={logo} className="App-logo" alt="logo" />
        <p>Upload File</p>
        <input className="file-input" type="file" onChange={onChange} />
        {!fileSelected && (
          <label htmlFor="file-input" className="button">
            Select File
          </label>
        )}
        {fileSelected && <p>Selected File URL: {urlFile}</p>}
      </div>
    </div>
  );
}

export default App;
