import React, { useEffect, useState } from 'react';
import './App.css';
import { Link, Switch, Route, BrowserRouter as Router } from 'react-router-dom';
import axios from 'axios';
import detectEthereumProvider from '@metamask/detect-provider';
import Web3 from 'web3';

import logo from './logo.svg';
import './App.css';
import logo1 from './vmLOGO.png';
import profile1 from'./profile.png';
import SoundWave from './soundwave1.png';
import wallet from './wallet.png';

import App2 from './App2';

function LinkButton() {
  return (
    <Link to="/app2">
      <button className="button">Go to App2</button>
    </Link>
  );
}

function App() {
  const [file, setFile] = useState(null);

  useEffect(() => {
    // Code to run on component mount

    // ...
  }, []);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    // Code to handle file upload

    // ...
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <img src={logo1} className="App-logo1" alt="logo1" />
          <div>
            <h1 className="App-title">Welcome to My App</h1>
            <p className="App-subtitle">Upload a file and go to App2</p>
          </div>
        </header>

        <div className="App-content">
          <div className="file-upload">
            <input type="file" onChange={handleFileChange} />
            <button className="button" onClick={handleUpload}>
              Upload File
            </button>
          </div>
          <LinkButton />
        </div>

        <Switch>
          <Route path="/app2" component={App2} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
